INSERT INTO public.tasks (id, parent_task_id, title, description, origin, root_id, position, resource_type, resource_url) VALUES 
('d99dec20-21a6-42c6-b3a6-5b0d4dc5289b', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Strategy -After clarifying the vision of your church you need to develop a strategy that will bring that vision to fruition (i.e. fundraising, marketing, outreach, staffing, discipleship, etc.)', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 1000, NULL, NULL),
('2736bf52-5813-4212-afa4-f7cb7a352b79', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Schedule – After developing a detailed launch plan, put dates to the actions within (i.e. develop a schedule timeline of when specific actions need to be completed to support your launch date).', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 2000, NULL, NULL),
('3ebeb9e6-2722-49ea-bb33-d637fbcf760d', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Launch – Start by defining “launch” in your context. There are several different definitions of launch. Some churches have a launch day, while others have a launch month. Some choose preview services with an official launch months later. Figure out what i', 'Launch – Start by defining “launch” in your context. There are several different definitions of launch. Some churches have a launch day, while others have a launch month. Some choose preview services with an official launch months later. Figure out what is best in your context.', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 3000, NULL, NULL),
('9272d201-a9f1-49bf-a159-ed7fa1461ace', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Delegation / Help – Don’t do anything in your launch plan that someone else can do. Delegate actions to your spouse, core team volunteers, part-time high school students, interns, staff, etc. There are many actions that can and should be completed by othe', 'Delegation / Help – Don’t do anything in your launch plan that someone else can do. Delegate actions to your spouse, core team volunteers, part-time high school students, interns, staff, etc. There are many actions that can and should be completed by others. Be creative.', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 4000, NULL, NULL),
('e4c62d24-4819-4ec9-8247-cfc31dbe77a1', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Core Team Development – The health of your church at launch will largely rise or fall on the size and commitment of your core team. Make it a priority to stay focused on people; particularly your core team. You will feel a constant tension between the see', 'Core Team Development – The health of your church at launch will largely rise or fall on the size and commitment of your core team. Make it a priority to stay focused on people; particularly your core team. You will feel a constant tension between the seemingly endless details that must be taken care of and spending time developing relationships.', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 5000, NULL, NULL),
('374dcf84-c7cc-46d7-a098-2c03ef82ada7', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Learn more…', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 6000, 'url', '/church-planting-tutorial-launch-plan/'),
('1b2ab69a-3c7b-41a0-a976-6a4f03271e96', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', '9. Philosophy of Ministry – Before developing a detailed plan of action to get your from where you are to where you want to be at launch, spend time developing a Philosophy of Ministry (purpose, values, beliefs, structure, strategy, priorities, etc.). Cli', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 7000, 'pdf', '/download/2173'),
('82d2d102-1266-4f1c-9c4c-367e8c554f76', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Church Location: Determine the city or area where the new church will be located. Visit the potential areas, and talk with other local churches.', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 8000, NULL, NULL),
('58a886f1-2619-4691-bbdb-9131edd8e7e4', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Target Audience: Develop a clear picture of the average family in your target area. Obtain demographic information to help assist you in defining who you will reach.', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 9000, NULL, NULL),
('6a6315b1-47e9-42fc-be8e-a72df8c78220', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Learn more…', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 10000, 'url', '/church-planting-tutorial-demographics/'),
('345a0eac-55a4-46f4-b2e1-f4f247e5da06', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Cut back on the number and type of actions to reduce spending', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 11000, NULL, NULL),
('d3994317-e0c1-40c8-875f-bb4dcda6da37', 'a04c7b3c-efcd-4b99-b023-ce3b70e559e4', 'Commit to raising additional funds to support the plan of action. Use the plan as a vision tool for raising more money. Revise your fundraising plan.', '', 'template', 'c7c589c3-86fb-4c1d-ae75-167df5c79463', 12000, NULL, NULL),
